INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1495227, 'Timolol', 'Oxtriphylline', 'Major', 'The pharmacologic effects of theophyllines and beta-blockers are opposite. Nonselective and high doses of cardioselective beta-blockers may cause severe or fatal bronchospasm by opposing theophylline-induced bronchodilation. Ophthalmic beta-blockers undergo significant systemic absorption and may also interact. In addition, propranolol and other beta-blockers may reduce the CYP450 hepatic metabolism of theophylline, and serum theophylline levels may be increased.', NULL, 'Oral and ophthalmic nonselective beta-blockers (e.', NULL, 'Oral and ophthalmic nonselective beta-blockers (e.g., carteolol, carvedilol, levobunolol, metipranolol, nadolol, oxprenolol, penbutolol, pindolol, propranolol, sotalol, and timolol) are considered contraindicated in patients with bronchospastic diseases. Cardioselective beta-blockers should generally be avoided, or used with extreme caution if no other alternatives are available and the benefits outweigh the risks of potentially severe bronchospasm. If patients do receive this combination, they should be closely monitored for increased serum theophylline levels but decreased bronchodilatory effectiveness.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1495228, 'Timolol', 'Pancuronium', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
