INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1495325, 'Tinidazole', 'Telaprevir', 'Moderate', 'Coadministration with telaprevir may increase the plasma concentrations of drugs that are metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by telaprevir.', 'DDInter', 1767317477, 'Caution is advised if telaprevir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever telaprevir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if telaprevir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1495326, 'Tipranavir', 'Tinidazole', 'Moderate', 'Ritonavir capsules, ritonavir oral solution, lopinavir-ritonavir oral solution, and tipranavir capsules all contain alcohol, which may produce a disulfiram-like reaction when coadministered with drugs that can inhibit aldehyde dehydrogenase (ALDH) such as nitroimidazoles (e.g., metronidazole, tinidazole), nitrofurans (e.g., furazolidone, nifurtimox), and cephalosporins with an N-methylthiotetrazole (NMTT) side chain that structurally resembles disulfiram. Following ingestion of alcohol, inhibition of ALDH results in increased concentrations of acetaldehyde, the accumulation of which can produce an unpleasant physiologic response referred to as the ''disulfiram reaction''. Symptoms include flushing, throbbing in head and neck, throbbing headache, respiratory difficulty, nausea, vomiting, sweating, thirst, chest pain, palpitation, dyspnea, hyperventilation, tachycardia, hypotension, syncope, weakness, vertigo, blurred vision, and confusion. Severe reactions may result in respiratory depression, cardiovascular collapse, arrhythmia, myocardial infarction, acute congestive heart failure, unconsciousness, convulsions, and death. The interaction is well established for disulfiram.', 'DDInter', 1767317477, 'Until further information is available, use of ritonavir capsules, ritonavir oral solution, lopinavir-ritonavir oral solution, or tipranavir capsules with nitroimidazoles (including vaginal formulations), nitrofurans, and certain cephalosporins should be avoided if possible.', 'Others', 'Until further information is available, use of ritonavir capsules, ritonavir oral solution, lopinavir-ritonavir oral solution, or tipranavir capsules with nitroimidazoles (including vaginal formulations), nitrofurans, and certain cephalosporins should be avoided if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1495327, 'Tocilizumab', 'Tinidazole', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', 'DDInter', 1767317477, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', NULL, NULL, 'Moderate', 'pharmacodynamic');
