INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1495341, 'Amprenavir', 'Tipranavir', 'Major', 'Coadministration with tipranavir plus low-dose ritonavir may significantly decrease the plasma concentrations of other protease inhibitors. The mechanism of interaction has not been described. Data are available for amprenavir, atazanavir, lopinavir, and saquinavir.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, use of other protease inhibitors in combination with tipranavir/ritonavir is not recommended.', 'Others', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, use of other protease inhibitors in combination with tipranavir/ritonavir is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
