INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1495349, 'Boceprevir', 'Tipranavir', 'Moderate', 'Coadministration of boceprevir and some ritonavir-boosted protease inhibitor regimens has been associated with decreased plasma concentrations of both boceprevir and the protease inhibitors. The mechanism of interaction has not been described. Data are currently unavailable regarding a potential interaction between boceprevir and other protease inhibitors, whether alone or ritonavir-boosted.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, caution is advised if boceprevir is used in combination with ritonavir-boosted protease inhibitor regimens. Patients coinfected with chronic HCV and HIV who have been started on one of these combinations should be closely monitored for treatment response and potential HCV and HIV virologic rebound. The safety and efficacy of boceprevir has not been established in the HCV/HIV coinfected population.', 'Others', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, caution is advised if boceprevir is used in combination with ritonavir-boosted protease inhibitor regimens.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1495350, 'Butabarbital', 'Tipranavir', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
