INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1495379, 'Etravirine', 'Tipranavir', 'Major', 'Coadministration with tipranavir/ritonavir may significantly decrease the plasma concentrations of etravirine. The mechanism of interaction has not been described but may involve tipranavir induction of CYP450 3A4 and ritonavir induction of CYP450 2C19 and 2C9, as etravirine is a substrate of these isoenzymes.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, the use of etravirine in combination with tipranavir/ritonavir should be avoided.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, the use of etravirine in combination with tipranavir/ritonavir should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
