INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1495383, 'Felodipine', 'Tipranavir', 'Moderate', 'Coadministration with tipranavir in combination with ritonavir may alter the pharmacokinetics of calcium channel blockers that are dual substrates of CYP450 3A4 and P-glycoprotein. The potential changes cannot be predicted due to the conflicting effects of tipranavir and ritonavir on CYP450 3A4 and P-glycoprotein. Specifically, ritonavir is a potent inhibitor of CYP450 3A4 and tend to reduce clearance of substrate drugs, while tipranavir is a potent inducer of P-glycoprotein intestinal efflux transporters and tend to increase clearance of substrate drugs.', 'DDInter', 1767317477, 'Caution is advised if calcium channel blockers such as diltiazem, felodipine, nicardipine, nisoldipine, and verapamil are coadministered with tipranavir/ritonavir. Patients should be monitored for potentially altered pharmacologic response to calcium channel blocker therapy following initiation or discontinuation of tipranavir/ritonavir.', 'Metabolism, Excretion', 'Caution is advised if calcium channel blockers such as diltiazem, felodipine, nicardipine, nisoldipine, and verapamil are coadministered with tipranavir/ritonavir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
