INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1495390, 'Garlic', 'Tipranavir', 'Major', 'Coadministration with garlic supplements may decrease the plasma concentrations of saquinavir and possibly other protease inhibitors (PIs). The exact mechanism is unknown but may involve induction of CYP450 3A4 metabolism and/or P-gycloprotein transport in the intestine by certain component(s) of garlic.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, garlic supplements should preferably be avoided in patients treated with PIs, particularly if they are using saquinavir as the sole PI in their antiretroviral regimen. Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, garlic supplements should preferably be avoided in patients treated with PIs, particularly if they are using saquinavir as the sole PI in their antiretroviral regimen.', NULL, NULL, 'Major', 'pharmacodynamic');
