    ) VALUES (1495699, 'Amphotericin B (cholesteryl sulfate)', 'Tobramycin', 'Moderate', 'The potential nephrotoxicity associated with amphotericin B and aminoglycosides may be additive.', NULL, 'Just before the start and just before the end of each amphotericin infusion, hydration of the patient with 500 mL of normal saline may be helpful.', NULL, 'Just before the start and just before the end of each amphotericin infusion, hydration of the patient with 500 mL of normal saline may be helpful. The patient''s renal function should be monitored frequently during therapy.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
