    ) VALUES (1495726, 'Gallium nitrate', 'Tobramycin', 'Major', 'Concomitant use of gallium nitrate and aminoglycosides may increase the risk that severe renal insufficiency will develop in patients with hypercalcemia of malignancy.', NULL, 'Adequate hydration and close monitoring of renal function are recommended.', NULL, 'Adequate hydration and close monitoring of renal function are recommended. The manufacturer recommends discontinuing gallium nitrate before starting therapy with other nephrotoxic agents.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
