INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496174, 'Dicoumarol', 'Tolcapone', 'Minor', 'Tolcapone may theoretically interfere with the CYP450 2C9 hepatic metabolism of oral anticoagulants. Based on interaction studies with tolcapone and other drugs, however, the clinical impact may be insignificant. The manufacturer recommends close monitoring of coagulation parameters if these drugs are coadministered. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496175, 'Dobutamine', 'Tolcapone', 'Moderate', 'COMT inhibitors affect the elimination of drugs metabolized by catechol-O-methyltransferase (COMT) and may potentiate their effects. Arrhythmias, increased heart rate, and blood pressure changes may occur. Entacapone has increased heart rates up to 80% when coadministered with isoproterenol and epinephrine. Ventricular tachycardia has also been reported.', 'DDInter', 1767317477, 'The manufacturer recommends that caution be exercised when these drugs are given together. Monitoring for changes in hemodynamic status and ECG is recommended.', 'Metabolism', 'The manufacturer recommends that caution be exercised when these drugs are given together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
