INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496367, 'Ginkgo biloba', 'Topiramate', 'Moderate', 'Certain preparations of ginkgo biloba have been reported to induce seizures and may antagonize the effects of anticonvulsants. Ginkgo products may contain varying amounts of 4''-O-methylpyridoxine (ginkgotoxin), a known neurotoxin found primarily in ginkgo biloba seeds but also detected in lesser amounts in the leaves. In vivo, 4''-O-methylpyridoxine competes with vitamin B6, which causes an indirect inhibition of glutamate decarboxylase and subsequent decrease in the formation of gamma-aminobutyric acid (GABA) in the brain.', 'DDInter', 1767317477, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. Because of inconsistencies in formulation and potency of commercial herbal preparations, there is no way to verify without laboratory testing if and in what quantity 4''-O-methylpyridoxine may be present in a given ginkgo preparation. Patients treated with anticonvulsants should preferably avoid the use of products containing ginkgo biloba.', 'Antagonism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496368, 'Glyburide', 'Topiramate', 'Moderate', 'Coadministration with topiramate may slightly reduce exposure to certain antidiabetic agents, such as pioglitazone and glyburide and their active metabolites. The mechanism for this interaction has not been delineated.', 'DDInter', 1767317477, 'Although these changes in exposure to pioglitazone and glyburide may not be clinically important, careful monitoring of glycemic control is recommended during concomitant therapy with topiramate.', 'Others', 'Although these changes in exposure to pioglitazone and glyburide may not be clinically important, careful monitoring of glycemic control is recommended during concomitant therapy with topiramate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496369, 'Glycerol phenylbutyrate', 'Topiramate', 'Moderate', 'Drugs that can increase plasma ammonia levels such as corticosteroids, haloperidol, or the anticonvulsants carbamazepine, topiramate, and phenobarbital may interfere with the therapeutic effects of phenylbutyrate therapy in the management of urea cycle disorders. Corticosteroids can cause the breakdown of body protein, which may lead to increased ammonia levels. The use of haloperidol has been associated with hyperammonemia in a young child with citrullinemia, an inherited disorder of ammonia excretion.', 'DDInter', 1767317477, 'Plasma ammonia levels should be closely monitored when corticosteroids, haloperidol, carbamazepine, topiramate, or phenobarbital are used in patients with urea cycle disorders receiving phenylbutyrate therapy.', 'Others', 'Plasma ammonia levels should be closely monitored when corticosteroids, haloperidol, carbamazepine, topiramate, or phenobarbital are used in patients with urea cycle disorders receiving phenylbutyrate therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
