INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496425, 'Fluvastatin', 'Toremifene', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of fluvastatin. According to the prescribing information, fluvastatin is primarily metabolized by CYP450 2C9 (approximately 75%) and, to a much lesser extent, by CYP450 2C8 and 3A4 (approximately 5% and 20%, respectively).', 'DDInter', 1767317477, 'Caution is advised when fluvastatin is prescribed with CYP450 2C9 inhibitors. The lowest starting dosage of fluvastatin is recommended, then titrated as needed based on clinical response and tolerance. Alternatively, pravastatin is not metabolized by CYP450 2C9 and may be a reasonable substitute for fluvastatin. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Caution is advised when fluvastatin is prescribed with CYP450 2C9 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
