INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496486, 'Toremifene', 'St. John''s Wort', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of toremifene.', 'DDInter', 1767317477, 'Concomitant use of toremifene with potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible during treatment with toremifene. If coadministration is required, a doubling of the toremifene dosage may be necessary depending on patient tolerability. Close monitoring for toxicities (e.g., QT prolongation, hepatotoxicity, hypercalcemia and tumor flare) is recommended if the dosage of toremifene is increased. The dosage should be reduced to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'Concomitant use of toremifene with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
