INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496629, 'Torasemide', 'Polythiazide', 'Moderate', 'The combination of a thiazide and loop diuretic may produce additive or synergistic effects on diuresis and excretion of electrolytes including sodium, potassium, magnesium, and chloride. Although these agents may be combined therapeutically in some patients with inadequate response to a single agent, the increased risk of dehydration, hypotension, hypokalemia, hypomagnesemia, and hyponatremia should be recognized.', 'DDInter', 1767317477, 'Caution is advised during concomitant use of a thiazide and loop diuretic. Dosages should be titrated slowly and carefully, and electrolytes, BUN, fluid status, blood pressure, and renal function should be monitored regularly. Patients should be advised to contact their physician if they experience signs and symptoms of fluid and electrolyte depletion such as dizziness, lightheadedness, dry mouth, thirst, fatigue, weakness, lethargy, muscle cramps, decreased urination, postural hypotension, and tachycardia.', 'Synergism', 'Caution is advised during concomitant use of a thiazide and loop diuretic.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496630, 'Torasemide', 'Prednisolone', 'Moderate', 'The concomitant use of corticosteroids and agents that deplete potassium (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in increased risk of hypokalemia. Corticosteroids can produce hypokalemia and other electrolyte disturbances via mineralocorticoid effects, the degree of which varies with the agent and route of administration (i.e. systemic vs. local).', 'DDInter', 1767317477, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496631, 'Torasemide', 'Probenecid', 'Minor', 'Some investigators suggest that probenecid may acutely decrease the pharmacologic effects of loop diuretics by competitively inhibiting their renal secretion in the proximal tubule. A marked increase in diuresis and natriuresis may then follow due to increased amount of diuretic available at renal acceptor sites once probenecid has been cleared from the urine.', 'DDInter', 1767317477, '-', 'Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
