INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496681, 'Bifidobacterium longum infantis', 'Trabectedin', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317477, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496682, 'Boceprevir', 'Trabectedin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of trabectedin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of trabectedin with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of trabectedin during and for 2 weeks after treatment with itraconazole. If a potent CYP450 3A4 inhibitor is required for short-term use (i.e., less than 14 days), it is advisable to administer the CYP450 3A4 inhibitor one week after the trabectedin infusion and discontinue it the day prior to the next infusion. Close monitoring for toxicities such as myelosuppression, rhabdomyolysis, hepatotoxicity, and cardiomyopathy is recommended during long-term coadministration, and the trabectedin dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Concomitant use of trabectedin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496683, 'Bromfenac', 'Trabectedin', 'Moderate', 'Coadministration of trabectedin with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', 'DDInter', 1767317477, 'The risk of hepatic injury should be considered when trabectedin is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of alkaline phosphatase, bilirubin, AST, and ALT should occur regularly during trabectedin treatment in accordance with the product labeling, or as often as necessary when clinical symptoms develop.', 'Synergism', 'The risk of hepatic injury should be considered when trabectedin is used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
