INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496714, 'Fenofibric acid', 'Trabectedin', 'Moderate', 'The risk of rhabdomyolysis may be increased during coadministration of trabectedin with other agents that are also associated with this toxicity, such as colchicine, alectinib, cobimetinib, statins, fibric acid derivatives, and lipid-lowering doses of niacin. The risk of liver injury may be increased during coadministration of trabectedin with other potentially hepatotoxic agents, including alectinib, cobimetinib, statins, fibric acid derivatives, and lipid-lowering doses of niacin.', 'DDInter', 1767317477, 'Caution is advised when trabectedin is used with other agents associated with rhabdomyolysis. Patients should be advised to seek medical attention if they experience unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Caution is advised when trabectedin is used with other agents that are potentially hepatotoxic. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of alkaline phosphatase, bilirubin, AST, and ALT should occur regularly during trabectedin treatment in accordance with the product labeling, or as often as necessary when clinical symptoms develop.', 'Synergism', 'Caution is advised when trabectedin is used with other agents associated with rhabdomyolysis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
