INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496723, 'Fluvastatin', 'Trabectedin', 'Moderate', 'The risk of rhabdomyolysis may be increased during coadministration of trabectedin with other agents that are also associated with this toxicity, such as colchicine, alectinib, cobimetinib, statins, fibric acid derivatives, and lipid-lowering doses of niacin. The risk of liver injury may be increased during coadministration of trabectedin with other potentially hepatotoxic agents, including alectinib, cobimetinib, statins, fibric acid derivatives, and lipid-lowering doses of niacin.', 'DDInter', 1767317477, 'Caution is advised when trabectedin is used with other agents associated with rhabdomyolysis. Patients should be advised to seek medical attention if they experience unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Caution is advised when trabectedin is used with other agents that are potentially hepatotoxic. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of alkaline phosphatase, bilirubin, AST, and ALT should occur regularly during trabectedin treatment in accordance with the product labeling, or as often as necessary when clinical symptoms develop.', 'Synergism', 'Caution is advised when trabectedin is used with other agents associated with rhabdomyolysis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496724, 'Fluvoxamine', 'Trabectedin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of trabectedin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised when trabectedin is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for toxicities such as myelosuppression, rhabdomyolysis, hepatotoxicity, and cardiomyopathy, and the trabectedin dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Caution is advised when trabectedin is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496725, 'Fosamprenavir', 'Trabectedin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of trabectedin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of trabectedin with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of trabectedin during and for 2 weeks after treatment with itraconazole. If a potent CYP450 3A4 inhibitor is required for short-term use (i.e., less than 14 days), it is advisable to administer the CYP450 3A4 inhibitor one week after the trabectedin infusion and discontinue it the day prior to the next infusion. Close monitoring for toxicities such as myelosuppression, rhabdomyolysis, hepatotoxicity, and cardiomyopathy is recommended during long-term coadministration, and the trabectedin dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Concomitant use of trabectedin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1496726, 'Fosaprepitant', 'Trabectedin', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of chemotherapeutic agents that are primarily metabolized by CYP450 3A4. The proposed mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant. In general, the effect of aprepitant on the pharmacokinetics of CYP450 3A4 substrates is expected to be greater when the substrates are administered orally as opposed to intravenously and may be altered following prolonged administration.', 'DDInter', 1767317477, 'Caution is advised if aprepitant or fosaprepitant is administered with chemotherapeutic agents that are primarily metabolized by CYP450 3A4, particularly those that were not studied extensively in premarketing trials. The potential for increased systemic toxicities of these agents should be considered. Chronic, continuous use of aprepitant for prevention of nausea and vomiting is not recommended because it has not been studied and because the drug interaction profile may change during long-term use.', 'Metabolism', 'Caution is advised if aprepitant or fosaprepitant is administered with chemotherapeutic agents that are primarily metabolized by CYP450 3A4, particularly those that were not studied extensively in premarketing trials.', NULL, NULL, 'Moderate', 'pharmacodynamic');
