INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497171, 'Etravirine', 'Tretinoin', 'Moderate', 'Coadministration with etravirine may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is accelerated clearance due to induction of CYP450 3A4 activity by etravirine.', 'DDInter', 1767317477, 'Caution is advised if etravirine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever etravirine is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if etravirine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497172, 'Fedratinib', 'Tretinoin', 'Moderate', 'Coadministration of fedratinib may increase the plasma concentrations of drugs that are substrates of CYP450 3A4, 2C19, or 2D6 isoenzymes.', 'DDInter', 1767317477, 'Caution is recommended when fedratinib is used concomitantly with substrates of CYP450 3A4, 2C19, or 2D6. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fedratinib is added to or withdrawn from therapy, particularly those with a narrow therapeutic index.', 'Metabolism', 'Caution is recommended when fedratinib is used concomitantly with substrates of CYP450 3A4, 2C19, or 2D6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
