INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497204, 'Esketamine', 'Triamcinolone (nasal)', 'Moderate', 'INTERVAL: Data are limited with respect to any potential interaction between intranasal esketamine and other nasal products during concomitant administration. In pharmacokinetic studies, mometasone furoate and oxymetazoline hydrochloride administered intranasally one hour before esketamine demonstrated no clinically significant effects on esketamine peak plasma concentration and exposure.', 'DDInter', 1767317477, 'To minimize the risk of interaction, patients who require a nasal corticosteroid or nasal decongestant on a day when esketamine dosing is scheduled should administer these medications at least one hour before esketamine.', 'Others', 'To minimize the risk of interaction, patients who require a nasal corticosteroid or nasal decongestant on a day when esketamine dosing is scheduled should administer these medications at least one hour before esketamine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
