INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497375, 'Triamterene', 'Potassium acetate', 'Major', 'The concomitant use of potassium-sparing diuretics and potassium preparations may result in hyperkalemia.', 'DDInter', 1767317477, 'The use of potassium preparations in patients treated with potassium-sparing diuretics is generally not recommended. Caution is advised if coadministration is required, particularly in patients with renal impairment, diabetes, old age, worsening heart failure, and/or a risk for dehydration. Serum potassium and renal function should be checked regularly, and oral potassium supplementation should be avoided in patients with severe congestive heart failure who have serum potassium levels above 3.5 mEq/L. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', 'The use of potassium preparations in patients treated with potassium-sparing diuretics is generally not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
