INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497699, 'Ezogabine', 'Trimipramine', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 1767317477, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497700, 'Fedratinib', 'Trimipramine', 'Moderate', 'Coadministration of fedratinib may increase the plasma concentrations of drugs that are substrates of CYP450 3A4, 2C19, or 2D6 isoenzymes.', 'DDInter', 1767317477, 'Caution is recommended when fedratinib is used concomitantly with substrates of CYP450 3A4, 2C19, or 2D6. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fedratinib is added to or withdrawn from therapy, particularly those with a narrow therapeutic index.', 'Metabolism', 'Caution is recommended when fedratinib is used concomitantly with substrates of CYP450 3A4, 2C19, or 2D6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497701, 'Felbamate', 'Trimipramine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497702, 'Fenfluramine', 'Trimipramine', 'Moderate', 'The coadministration of fenfluramine and tricyclic antidepressants (TCAs) may result in elevated plasma concentrations of the latter. The mechanism is decreased clearance of TCAs due to inhibition of CYP450 enzymatic activities by fenfluramine. In addition, the combination of fenfluramine with a TCA may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5HT1A receptors.', 'DDInter', 1767317477, 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk. Close monitoring is recommended for signs and symptoms of TCA toxicity as well as excessive serotonergic activity such as CNS irritability, altered consciousness, confusion, myoclonus, ataxia, abdominal cramping, hyperpyrexia, shivering, pupillary dilation, diaphoresis, hypertension, and tachycardia.', 'Metabolism', 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497703, 'Fenoldopam', 'Trimipramine', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317477, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497704, 'Fesoterodine', 'Trimipramine', 'Moderate', 'Agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive effects when used in combination. Excessive parasympatholytic effects may result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome. Peripheral symptoms of intoxication commonly include mydriasis, blurred vision, flushed face, fever, dry skin and mucous membranes, tachycardia, urinary retention, and constipation. Central symptoms may include memory loss, disorientation, incoherence, hallucinations, psychosis, delirium, hyperactivity, twitching or jerking movements, stereotypy, and seizures. Central nervous system-depressant effects may also be additively or synergistically increased when these agents are combined, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.', 'Synergism', 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497705, 'Fingolimod', 'Trimipramine', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of fingolimod treatment in patients receiving drugs that prolong the QT interval. Fingolimod can cause a decrease in heart rate during initiation of therapy that is apparent within an hour of the first dose and maximal at approximately 6 hours postdose in most cases, but occasionally up to 20 hours after the first dose.', 'DDInter', 1767317477, 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, close monitoring is recommended during initiation of fingolimod treatment in patients receiving concomitant drugs that can prolong the QT interval, patients with significant QT prolongation (QTc >470 msec in females or >450 msec in males), or patients with relevant risk factors for QT prolongation (e.g., hypokalemia, hypomagnesemia, congenital QT prolongation).', 'Synergism', 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497706, 'Flavoxate', 'Trimipramine', 'Moderate', 'Agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive effects when used in combination. Excessive parasympatholytic effects may result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome. Peripheral symptoms of intoxication commonly include mydriasis, blurred vision, flushed face, fever, dry skin and mucous membranes, tachycardia, urinary retention, and constipation. Central symptoms may include memory loss, disorientation, incoherence, hallucinations, psychosis, delirium, hyperactivity, twitching or jerking movements, stereotypy, and seizures. Central nervous system-depressant effects may also be additively or synergistically increased when these agents are combined, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.', 'Synergism', 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497707, 'Flecainide', 'Trimipramine', 'Moderate', 'Coadministration with Class IC antiarrhythmic agents may increase the plasma concentrations of some tricyclic antidepressants (TCAs). The proposed mechanism is inhibition of CYP450 2D6, the isoenzyme primarily or partially responsible for the metabolism of most TCAs. Class IC antiarrhythmic agents can cause prolongation of the QT interval. Theoretically, coadministration with other agents such as TCAs that can prolong the QT interval may increase the risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential related to their effects on cardiac conduction.', 'DDInter', 1767317477, 'Caution and clinical monitoring are recommended if a class IC antiarrhythmic agent is prescribed in combination with a tricyclic antidepressant. Pharmacologic response and serum TCA levels should be monitored more closely whenever a class IC antiarrhythmic agent is added to or withdrawn from therapy, and the TCA dosage adjusted as necessary. Patients should be advised to notify their physician if they experience possible signs and symptoms of TCA toxicity such as excessive sedation, dry mouth, blurred vision, urinary retention, constipation, tachycardia, arrhythmia, and seizures. Patients should seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Metabolism, Synergism', 'Caution and clinical monitoring are recommended if a class IC antiarrhythmic agent is prescribed in combination with a tricyclic antidepressant.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497708, 'Flibanserin', 'Trimipramine', 'Moderate', 'The central nervous system (CNS) effects of flibanserin such as somnolence and fatigue may be potentiated by concomitant use of other agents with CNS depressant effects. The risk of CNS depression is increased if flibanserin is taken during waking hours or with alcohol or other CNS depressants.', 'DDInter', 1767317477, 'Patients receiving flibanserin with other agents that can cause CNS depression should be apprised of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness, and fatigue. Patients should not drive, operate machinery, or engage in hazardous activities requiring mental alertness and motor coordination for at least 6 hours after taking flibanserin.', 'Synergism', 'Patients receiving flibanserin with other agents that can cause CNS depression should be apprised of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness, and fatigue.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497709, 'Fluconazole', 'Trimipramine', 'Moderate', 'The coadministration with azole antifungal agents may increase the plasma concentrations of some tricyclic antidepressants (TCAs). Elevated TCA levels may result in CNS toxicity, anticholinergic symptoms, and QT interval prolongation. The mechanism is decreased TCA clearance due to azole-related inhibition of the CYP450 3A4 isoenzyme, which is partially responsible for the metabolism of TCAs like amitriptyline, clomipramine, imipramine, and possibly others.', 'DDInter', 1767317477, 'During concomitant therapy with azole antifungal agents, patients stabilized on their TCA regimen should be observed for increased pharmacologic response to the TCA, and the dosage adjusted accordingly if necessary. Serum TCA levels should be monitored following initiation or discontinuation of azole antifungal therapy. Patients should be advised to notify their caregiver if they experience excessive dry mouth, visual disturbances, urinary retention, dizziness, syncope, fast or irregular heartbeat, constipation, or seizures.', 'Metabolism', 'During concomitant therapy with azole antifungal agents, patients stabilized on their TCA regimen should be observed for increased pharmacologic response to the TCA, and the dosage adjusted accordingly if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497710, 'Flumazenil', 'Trimipramine', 'Major', 'Severe adverse effects, including fatalities, have been reported following the administration of flumazenil to multiple-drug-overdose patients who have taken large quantities of tricyclic antidepressants. The effects have included seizures and arrhythmias (including ventricular tachycardia). The mechanism of this interaction may be related to a decrease in the protective antiseizure effects of benzodiazepines that patients may have taken concomitantly with overdoses of tricyclic antidepressants.', 'DDInter', 1767317477, 'Administration of flumazenil should be avoided in patients who are known or suspected to have received overdoses of tricyclic antidepressants.', 'Synergism', 'Administration of flumazenil should be avoided in patients who are known or suspected to have received overdoses of tricyclic antidepressants.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497711, 'Fluphenazine', 'Trimipramine', 'Moderate', 'Coadministration of a phenothiazine with a tricyclic antidepressant (TCA) may result in elevated plasma concentrations of one or both drugs as well as additive adverse effects. Most phenothiazines and TCAs have been found to undergo metabolism by CYP450 2D6, thus competitive inhibition of the enzyme may occur when more than one of these agents are administered.', 'DDInter', 1767317477, 'Concurrent use of phenothiazines and TCAs should be approached with caution, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication (e.g., abdominal pain, fever, heat intolerance, blurred vision, confusion, hallucinations) or cardiovascular toxicity (e.g., dizziness, palpitations, arrhythmias, syncope). Ambulatory patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Metabolism', 'Concurrent use of phenothiazines and TCAs should be approached with caution, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497712, 'Flurazepam', 'Trimipramine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497713, 'Flutamide', 'Trimipramine', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 1767317477, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497714, 'Fluvoxamine', 'Trimipramine', 'Major', 'Coadministration with fluvoxamine may significantly increase the plasma concentrations of some tricyclic antidepressants (TCAs). The proposed mechanism is fluvoxamine inhibition of multiple hepatic microsomal enzymes involved in the metabolic clearance of TCAs. Pharmacodynamically, the combination of fluvoxamine (or any other selective serotonin reuptake inhibitor) and a TCA may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5HT1A receptors.', 'DDInter', 1767317477, 'In general, the use of fluvoxamine (or other SSRIs) with TCAs should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk. Pharmacologic response and plasma TCA levels should be monitored more closely whenever fluvoxamine is added to or withdrawn from therapy in patients stabilized on their existing antidepressant regimen, and the TCA dosage adjusted as necessary. Patients should be monitored closely for signs and symptoms of TCA toxicity (e.g., sedation, dry mouth, blurred vision, constipation, urinary retention) and/or excessive serotonergic activity (e.g., CNS irritability, altered consciousness, confusion, myoclonus, ataxia, abdominal cramping, hyperpyrexia, shivering, pupillary dilation, diaphoresis, hypertension, and tachycardia).', 'Metabolism', 'In general, the use of fluvoxamine (or other SSRIs) with TCAs should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497715, 'Foscarnet', 'Trimipramine', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 1767317477, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497716, 'Fosinopril', 'Trimipramine', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317477, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497717, 'Fosphenytoin', 'Trimipramine', 'Moderate', 'Tricyclic antidepressants may increase serum phenytoin levels. In vitro studies suggest the mechanism may involve inhibition of CYP450 2C19-catalyzed phenytoin p-hydroxylation by tricyclic antidepressants, especially imipramine and amitriptyline. Phenytoin may induce the hepatic metabolism of desipramine resulting in decreased plasma concentrations.', 'DDInter', 1767317477, 'Clinical monitoring of patient response, tolerance, and serum phenytoin concentrations is recommended. Patients should be advised to notify their doctor if they experience loss of seizure control or symptoms of phenytoin toxicity (drowsiness, visual disturbances, change in mental status, nausea, or ataxia). Phenytoin dosage may need to be adjusted.', 'Metabolism', 'Clinical monitoring of patient response, tolerance, and serum phenytoin concentrations is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497718, 'Fospropofol', 'Trimipramine', 'Moderate', 'The concomitant use of general anesthetics with tricyclic antidepressants may increase the risk of hypotension and cardiac arrhythmias. The presence of pancuronium may increase the risk of tachyarrhythmias. Enflurane may also increase the risk of seizures when given to patients who have been taking tricyclic antidepressants.', 'DDInter', 1767317477, 'Close monitoring is recommended when these drugs are used together.', 'Synergism', 'Close monitoring is recommended when these drugs are used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497719, 'Fostemsavir', 'Trimipramine', 'Moderate', 'Fostemsavir can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval. Consider obtaining electrocardiogram and serum electrolyte levels prior to initiating fostemsavir therapy and periodically thereafter, particularly in patients with risk factors for torsade de pointes and/or receiving one or more potent CYP450 3A4 inhibitors (e.g., azole antifungal agents; cobicistat; protease inhibitors; ketolide and certain macrolide antibiotics; ceritinib; idelalisib; nefazodone) or P-gp inhibitors (e.g., cyclosporine; diltiazem; quinidine; quinine; tacrolimus; verapamil). Correct hypokalemia and/or hypomagnesemia before starting treatment and as indicated during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497720, 'Furosemide', 'Trimipramine', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317477, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497721, 'Gabapentin', 'Trimipramine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497722, 'Galantamine', 'Trimipramine', 'Moderate', 'Due to opposing effects, agents that possess anticholinergic activity (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; class IA antiarrhythmics especially disopyramide; carbamazepine; cimetidine; ranitidine) may negate the already small pharmacologic benefits of acetylcholinesterase inhibitors in the treatment of dementia. These agents may also adversely affect elderly patients in general. Clinically significant mental status changes associated with anticholinergic agents can range from mild cognitive impairment to delirium, and patients with Alzheimer''s disease and other dementia are especially sensitive.', 'DDInter', 1767317477, 'Drugs that possess anticholinergic activity should generally be avoided in patients with Alzheimer''s disease or other cognitive impairment, regardless of whether they are receiving an acetylcholinesterase inhibitor. For patients requiring treatment to counteract adverse effects of acetylcholinesterase inhibitor therapy (e.g., gastrointestinal intolerance, urinary problems), an agent without anticholinergic properties should be used whenever possible. Otherwise, a dosage reduction, slower titration, or even discontinuation of the acetylcholinesterase inhibitor should be considered. For patients who are already receiving an acetylcholinesterase inhibitor with anticholinergic agents, every attempt should be made to discontinue the latter or substitute them with less anticholinergic alternatives.', 'Antagonism', 'Drugs that possess anticholinergic activity should generally be avoided in patients with Alzheimer''s disease or other cognitive impairment, regardless of whether they are receiving an acetylcholinesterase inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497723, 'Gatifloxacin', 'Trimipramine', 'Major', 'Certain quinolones, including gatifloxacin and moxifloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 1767317477, 'Coadministration of gatifloxacin or moxifloxacin with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of the quinolone, recommended dosages and intravenous infusion rates should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of gatifloxacin or moxifloxacin with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497724, 'Gemifloxacin', 'Trimipramine', 'Moderate', 'Certain quinolones, including gemifloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Although the risk of a serious interaction is probably low, caution is recommended if gemifloxacin is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation may increase with increasing plasma concentrations of gemifloxacin, the recommended dosage should not be exceeded, especially in patients with renal or hepatic impairment.', 'Synergism', 'Although the risk of a serious interaction is probably low, caution is recommended if gemifloxacin is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497725, 'Gilteritinib', 'Trimipramine', 'Moderate', 'Gilteritinib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Caution and clinical monitoring are recommended if gilteritinib is used in combination with other drugs that can prolong the QT interval. Patients should have electrocardiograms (ECGs) performed prior to initiation of treatment with gilteritinib, on days 8 and 15 of cycle 1, and prior to the start of the next two subsequent cycles. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended if gilteritinib is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497726, 'Ginkgo biloba', 'Trimipramine', 'Moderate', 'Certain preparations of ginkgo biloba have been reported to induce seizures. There may be a theoretical risk of increased seizure potential when used with other agents that can lower the seizure threshold such as selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, and theophylline. Ginkgo products may contain varying amounts of 4''-O-methylpyridoxine (ginkgotoxin), a known neurotoxin found primarily in ginkgo biloba seeds but also detected in lesser amounts in the leaves. In vivo, 4''-O-methylpyridoxine competes with vitamin B6, which causes an indirect inhibition of glutamate decarboxylase and subsequent decrease in the formation of gamma-aminobutyric acid (GABA) in the brain. There have been published case reports of generalized convulsions and vomiting within several hours after ingestion of large amounts of ginkgo nuts/seeds, including in young children and healthy individuals with no known personal or family history of epilepsy.', 'DDInter', 1767317477, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. Because of inconsistencies in formulation and potency of commercial herbal preparations, there is no way to verify without laboratory testing if and in what quantity 4''-O-methylpyridoxine may be present in a given ginkgo preparation. Patients treated with agents that can lower the seizure threshold should preferably avoid the use of products containing ginkgo biloba.', 'Synergism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497727, 'Givosiran', 'Trimipramine', 'Moderate', 'Coadministration with givosiran may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 1A2 and/or CYP450 2D6. The proposed mechanism is decreased clearance due to givosiran-mediated inhibition of CYP450 1A2 and 2D6 isoenzymes.', 'DDInter', 1767317477, 'Caution and monitoring are recommended when givosiran is given with drugs that are substrates of CYP450 1A2 and/or 2D6. Concomitant use should generally be avoided with CYP450 1A2 or 2D6 substrates that have a narrow therapeutic range where minimal concentration changes may lead to serious or life-threatening toxicities. If concomitant use is unavoidable, the dosage of the CYP450 1A2 or 2D6 substrate should be reduced according to approved product labeling or clinical response and tolerance. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever givosiran is added to or withdrawn from therapy with these drugs.', 'Metabolism', 'Caution and monitoring are recommended when givosiran is given with drugs that are substrates of CYP450 1A2 and/or 2D6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497728, 'Glasdegib', 'Trimipramine', 'Moderate', 'Glasdegib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Coadministration of glasdegib with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Patients should have electrocardiograms (ECGs) performed prior to initiation of treatment with glasdegib, approximately one week after initiation, and then once monthly for the next two months, although patients with risk factors may require more frequent and ongoing ECG monitoring.', 'Synergism', 'Coadministration of glasdegib with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497729, 'Glipizide', 'Trimipramine', 'Moderate', 'Case reports suggest that tricyclic antidepressants may increase the hypoglycemic effects of sulfonylureas.', 'DDInter', 1767317477, 'Patients receiving this combination should be advised to regularly monitor their blood sugar, counseled on how to recognize and treat hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, tremor, hunger, weakness, or palpitations) and to notify their physician if it occurs. The sulfonylurea dosage may require reduction in affected patients.', 'Synergism', 'Patients receiving this combination should be advised to regularly monitor their blood sugar, counseled on how to recognize and treat hypoglycemia (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497730, 'Glyburide', 'Trimipramine', 'Moderate', 'Case reports suggest that tricyclic antidepressants may increase the hypoglycemic effects of sulfonylureas.', 'DDInter', 1767317477, 'Patients receiving this combination should be advised to regularly monitor their blood sugar, counseled on how to recognize and treat hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, tremor, hunger, weakness, or palpitations) and to notify their physician if it occurs. The sulfonylurea dosage may require reduction in affected patients.', 'Synergism', 'Patients receiving this combination should be advised to regularly monitor their blood sugar, counseled on how to recognize and treat hypoglycemia (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497731, 'Glycerol phenylbutyrate', 'Trimipramine', 'Moderate', 'Coadministration of glycerol phenylbutyrate may lead to increased concentrations of CYP450 2D6 substrates. The proposed mechanism is inhibition of CYP450 2D6 by phenylbutyrate, the active moiety of glycerol phenylbutyrate, which has been shown to be an in vitro inhibitor of this isoenzyme.', 'DDInter', 1767317477, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate.', 'Metabolism', 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol).', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497732, 'Glycopyrronium', 'Trimipramine', 'Moderate', 'Agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive effects when used in combination. Excessive parasympatholytic effects may result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome. Peripheral symptoms of intoxication commonly include mydriasis, blurred vision, flushed face, fever, dry skin and mucous membranes, tachycardia, urinary retention, and constipation. Central symptoms may include memory loss, disorientation, incoherence, hallucinations, psychosis, delirium, hyperactivity, twitching or jerking movements, stereotypy, and seizures. Central nervous system-depressant effects may also be additively or synergistically increased when these agents are combined, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.', 'Synergism', 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497733, 'Glycopyrronium (topical)', 'Trimipramine', 'Moderate', 'The potential exists for additive anticholinergic effects such as mydriasis, blurred vision, heat intolerance, fever, dry mouth, tachycardia, urinary retention, constipation, and glaucoma (onset or exacerbation) when topical or inhaled anticholinergic agents are used with each other or with other agents that possess anticholinergic properties. The risk of systemic anticholinergic effects following topical administration depends on variables such as strength of the product, size of the application area, frequency of application, and use of occlusive dressing. Systemic effects are uncommon following oral inhalation or nasal administration due to the poor absorption of quaternary ammonium compounds from gastrointestinal and nasal mucosa.', 'DDInter', 1767317477, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide). Caution is advised if concomitant use cannot be avoided, particularly in the elderly and those with significantly impaired renal and/or hepatic function.', 'Synergism', 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497734, 'Goserelin', 'Trimipramine', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 1767317477, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497735, 'Granisetron', 'Trimipramine', 'Major', 'Concomitant use of 5-HT3 receptor antagonists with agents that possess or enhance serotonergic activity such as antidepressants and lithium may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Treatment with 5-HT3 receptor antagonists has been associated with dose-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval including certain antidepressants and lithium may result in additive effects and increased risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Caution is advised if 5-HT3 receptor antagonists are prescribed with other agents that affect the serotonergic neurotransmitter system. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is warranted when initiating or increasing the dosages of these agents. In addition, the potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary. Moderately ill patients may also benefit from the administration of a serotonin antagonist (e.g., cyproheptadine, chlorpromazine). Severe cases should be managed under consultation with a toxicologist and may require sedation, neuromuscular paralysis, intubation, and mechanical ventilation in addition to the other measures. Due to the potential for additive effects on the QT interval, ECG monitoring may also be appropriate when 5-HT3 receptor antagonists are used with certain antidepressants or lithium. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised if 5-HT3 receptor antagonists are prescribed with other agents that affect the serotonergic neurotransmitter system.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497736, 'Grepafloxacin', 'Trimipramine', 'Major', 'Grepafloxacin can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Coadministration of grepafloxacin with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', 'Coadministration of grepafloxacin with other drugs that can prolong the QT interval is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497737, 'Guanabenz', 'Trimipramine', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317477, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497738, 'Guanadrel', 'Trimipramine', 'Moderate', 'Tricyclic antidepressants (TCAs) may reduce the antihypertensive effects of guanethidine. Loss of blood pressure control may result. The mechanism is TCA-induced inhibition of guanethidine uptake into adrenergic neurons. Hypotensive reactions may occur when the tricyclic antidepressant is discontinued. Other adrenergic neurone blockers may also be affected.', 'DDInter', 1767317477, 'Either a different antidepressant or a different antihypertensive agent (such as an ACE inhibitor or beta-blocker) should be considered. If patients do receive this combination, their blood pressure should be closely monitored when initiating or discontinuing either drug.', 'Antagonism', 'Either a different antidepressant or a different antihypertensive agent (such as an ACE inhibitor or beta-blocker) should be considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497739, 'Guanethidine', 'Trimipramine', 'Moderate', 'Tricyclic antidepressants (TCAs) may reduce the antihypertensive effects of guanethidine. Loss of blood pressure control may result. The mechanism is TCA-induced inhibition of guanethidine uptake into adrenergic neurons. Hypotensive reactions may occur when the tricyclic antidepressant is discontinued. Other adrenergic neurone blockers may also be affected.', 'DDInter', 1767317477, 'Either a different antidepressant or a different antihypertensive agent (such as an ACE inhibitor or beta-blocker) should be considered. If patients do receive this combination, their blood pressure should be closely monitored when initiating or discontinuing either drug.', 'Antagonism', 'Either a different antidepressant or a different antihypertensive agent (such as an ACE inhibitor or beta-blocker) should be considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497740, 'Guanfacine', 'Trimipramine', 'Moderate', 'One case report suggests that tricyclic antidepressants may decrease the antihypertensive effect of guanfacine. The mechanism is unknown.', 'DDInter', 1767317477, 'The patient''s blood pressure should be monitored during concomitant therapy and after discontinuation or dose change of the antidepressant. An alternative antihypertensive (e.g., beta adrenergic blocking agent, diuretic) may be considered.', 'Others', 'The patient''s blood pressure should be monitored during concomitant therapy and after discontinuation or dose change of the antidepressant.', NULL, NULL, 'Moderate', 'pharmacodynamic');
