INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497769, 'Trimipramine', 'Mazindol', 'Moderate', 'The use of tricyclic antidepressants in combination with amphetamines or sympathomimetic appetite suppressants may produce additive cardiovascular effects, increasing the risk of hypertension, cardiac arrhythmias, tachycardia, and fever. The mechanism involves additive pharmacodynamic effects resulting from increased norepinephrine release by sympathomimetic agents and inhibition of norepinephrine reuptake by tricyclic antidepressants. A pharmacokinetic interaction is also possible between tricyclic antidepressants and amphetamines, since many of these agents are metabolized by CYP450 2D6. Increased plasma levels of one or both drugs may occur during coadministration.', 'DDInter', 1767317477, 'Close monitoring of cardiovascular status is recommended for patients receiving this combination. Patients should be advised to promptly report symptoms such as fever, headache, or fast or irregular heartbeats.', 'Metabolism, Synergism', 'Close monitoring of cardiovascular status is recommended for patients receiving this combination.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497770, 'Trimipramine', 'Mephenytoin', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
