INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497828, 'Vandetanib', 'Trimipramine', 'Major', 'Vandetanib can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Coadministration of vandetanib with other drugs that can prolong the QT interval should generally be avoided. Should treatment with other QT-prolonging drugs be required, the manufacturer recommends more frequent monitoring of the ECG as well as serum potassium, magnesium, and calcium levels.', 'Synergism', 'Coadministration of vandetanib with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497829, 'Vasopressin', 'Trimipramine', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 1767317477, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
