INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497885, 'Histamine', 'Triprolidine', 'Moderate', 'Antihistamines may suppress the patient''s response to histamine phosphate. The length of suppression varies and is dependent on the patient, type of antihistamine, and the duration of antihistamine therapy. The duration of suppression may range from 24 hours with chlorpheniramine or up to several weeks with second-generation antihistamines.', 'DDInter', 1767317477, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering histamine phosphate to patients treated with antihistamines.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering histamine phosphate to patients treated with antihistamines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
