INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497932, 'Zolpidem', 'Triprolidine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1497933, 'Tuberculin purified protein derivative', 'Astemizole', 'Moderate', 'INTERVAL: Antihistamines may diminish the response to diagnostic skin test antigens. Falsely insignificant or false-negative results may occur in such patients.', 'DDInter', 1767317477, 'Antihistamines should be discontinued approximately 2 to 4 days prior to skin testing procedures.', 'Others', 'Antihistamines should be discontinued approximately 2 to 4 days prior to skin testing procedures.', NULL, NULL, 'Moderate', 'pharmacodynamic');
