    ) VALUES (1498009, 'Aminoglutethimide', 'Tucatinib', 'Moderate', 'Coadministration with CYP450 3A4 inducers may decrease the plasma concentrations of tucatinib, which is primarily metabolized by CYP450 2C8 and to a lesser extent by CYP450 3A4.', NULL, 'The potential for diminished pharmacologic effects of tucatinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, 'The potential for diminished pharmacologic effects of tucatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
