    ) VALUES (1498010, 'Aminophylline', 'Tucatinib', 'Moderate', 'Coadministration with tucatinib may increase the plasma concentrations of CYP450 3A4 substrates. The mechanism involves tucatinib-mediated inhibition of CYP450 3A isoenzymes.', NULL, 'Concomitant use of tucatinib with CYP450 3A substrates, particularly those with a narrow therapeutic index, should be avoided.', NULL, 'Concomitant use of tucatinib with CYP450 3A substrates, particularly those with a narrow therapeutic index, should be avoided. If coadministration is required, a reduction in the dosage of the CYP450 3A substrate in accordance with product labeling is recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
