    ) VALUES (1499189, 'Cisapride', 'Venlafaxine', 'Major', 'Cisapride can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of cisapride with other drugs that can prolong the QT interval is considered contraindicated.', NULL, 'Coadministration of cisapride with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
