INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1499500, 'Butabarbital', 'Vilazodone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of vilazodone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The possibility of a diminished therapeutic response to vilazodone should be considered during coadministration with CYP450 3A4 inducers. Pharmacologic effects of vilazodone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'The possibility of a diminished therapeutic response to vilazodone should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1499501, 'Cerivastatin', 'Vilazodone', 'Moderate', 'Coadministration with vilazodone may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 2C8. Vilazodone has been shown to be an inhibitor of CYP450 2C8 in vitro. However, there are no clinical data on concomitant use with drugs that are substrates of the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised if vilazodone is used concurrently with medications that undergo metabolism by CYP450 2C8, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever vilazodone is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if vilazodone is used concurrently with medications that undergo metabolism by CYP450 2C8, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
