INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1499581, 'Vilazodone', 'Phenobarbital', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of vilazodone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The possibility of diminished therapeutic response to vilazodone should be considered during coadministration with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, and rifampin. When concomitant use is prescribed for greater than 14 days, an increase in the vilazodone dosage up to 2-fold may be considered depending on clinical response, up to a maximum of 80 mg daily. Following discontinuation of the CYP450 3A4 inducer, the dosage of vilazodone should be returned to the original level in 14 days.', 'Metabolism', 'The possibility of diminished therapeutic response to vilazodone should be considered during coadministration with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, and rifampin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
