INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1499645, 'Ethotoin', 'Vinorelbine', 'Moderate', 'Cases have been reported in which patients receiving chemotherapy have experienced markedly reduced plasma phenytoin concentrations and seizures. Other hydantoins may interact with chemotherapy in a similar manner. The mechanism of this interaction has not been clearly established.', 'DDInter', 1767317477, 'It may be necessary to increase phenytoin dosage during chemotherapy and to decrease it after or between courses of chemotherapy. Intravenous phenytoin may be less likely to interact with chemotherapy. Careful monitoring of patients for seizure activity is recommended, as is monitoring of plasma phenytoin levels.', 'Others', 'It may be necessary to increase phenytoin dosage during chemotherapy and to decrease it after or between courses of chemotherapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
