    ) VALUES (1499806, 'Aminoglutethimide', 'Voriconazole', 'Moderate', 'Coadministration with inducers of CYP450 isoenzymes may decrease the plasma concentrations of voriconazole, which is metabolized by CYP450 2C19, 2C9, and/or 3A4.', NULL, 'The potential for diminished pharmacologic effects of voriconazole should be considered during coadministration with CYP450 inducers.', NULL, 'The potential for diminished pharmacologic effects of voriconazole should be considered during coadministration with CYP450 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
