INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1499881, 'Fosphenytoin', 'Voriconazole', 'Major', 'Coadministration of phenytoin and voriconazole may significantly reduce the plasma concentrations of voriconazole and increase the plasma concentrations of phenytoin. The mechanism involves phenytoin induction of voriconazole metabolism via CYP450 2C19, 2C9 and 3A4, and voriconazole inhibition of phenytoin metabolism via CYP450 2C9 and 2C19.', NULL, 'During concomitant therapy with phenytoin, the manufacturer recommends that maintenance dosage of voriconazole be increased from 4 mg/kg to 5 mg/kg every 12 hours when given intravenously, and from 200 mg to 400 mg every 12 hours when given orally (or from 100 mg to 200 mg every 12 hours in patients who weigh less than 40 kg).', NULL, 'During concomitant therapy with phenytoin, the manufacturer recommends that maintenance dosage of voriconazole be increased from 4 mg/kg to 5 mg/kg every 12 hours when given intravenously, and from 200 mg to 400 mg every 12 hours when given orally (or from 100 mg to 200 mg every 12 hours in patients who weigh less than 40 kg). Frequent monitoring of plasma phenytoin levels and phenytoin-related adverse effects is recommended. Patients should be advised to contact their doctor if they experience symptoms of phenytoin toxicity such as nausea, vomiting, tremor, ataxia, lethargy, slurred speech, visual disturbances, and/or changes in mental status. Some experts also recommend monitoring plasma voriconazole levels, particularly in critically ill patients. Inadequate levels of voriconazole in the presence of phenytoin have been reported rarely, despite increasing voriconazole dosage as recommended. Additionally, measuring voriconazole levels following withdrawal of phenytoin can help to determine when to readjust voriconazole dosage.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1499882, 'Fostamatinib', 'Voriconazole', 'Major', 'Coadministration of fostamatinib with potent inhibitors of CYP450 3A4 may significantly increase exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration. Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9. Increased exposure to R406 may result in increased risk of adverse effects such as diarrhea, hepatotoxicity, hypertension, and neutropenia.', NULL, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent CYP450 3A4 inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', NULL, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent CYP450 3A4 inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1499883, 'Fostemsavir', 'Voriconazole', 'Moderate', 'Fostemsavir can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval. Consider obtaining electrocardiogram and serum electrolyte levels prior to initiating fostemsavir therapy and periodically thereafter, particularly in patients with risk factors for torsade de pointes and/or receiving one or more potent CYP450 3A4 inhibitors (e.g., azole antifungal agents; cobicistat; protease inhibitors; ketolide and certain macrolide antibiotics; ceritinib; idelalisib; nefazodone) or P-gp inhibitors (e.g., cyclosporine; diltiazem; quinidine; quinine; tacrolimus; verapamil). Correct hypokalemia and/or hypomagnesemia before starting treatment and as indicated during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
