    ) VALUES (1499952, 'Voriconazole', 'Telaprevir', 'Moderate', 'Coadministration with voriconazole may increase the plasma concentrations of telaprevir. The mechanism involves voriconazole inhibition of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of telaprevir. The interaction has not been specifically studied with voriconazole, but has been reported with another azole antifungal agent.', NULL, 'Voriconazole should not be administered to patients receiving telaprevir unless the benefits are anticipated to outweigh the risks.', NULL, 'Voriconazole should not be administered to patients receiving telaprevir unless the benefits are anticipated to outweigh the risks.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
