INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500141, 'Voxelotor', 'Levobupivacaine', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of levobupivacaine, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317477, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.g., quinolones, deferasirox, mexiletine, propafenone, ticlopidine, zileuton) or CYP450 3A4 (e.g., azole antifungals, macrolide antibiotics, protease inhibitors, kinase inhibitors, aprepitant, conivaptan, cyclosporine, delavirdine, diltiazem, nefazodone, verapamil). Particular caution and dosage adjustment of levobupivacaine may be warranted during concomitant use of both a CYP450 1A2 inhibitor and a CYP450 3A4 inhibitor, or a drug that inhibits both isoenzymes (e.g., fluvoxamine). Patients should be monitored for early signs and symptoms of central nervous system toxicity such as dizziness, drowsiness, lightheadedness, anxiety, restlessness, incoherent speech, tremors, twitching, numbness and tingling of the mouth and lips, metallic taste, tinnitus, and blurred vision, as well as more serious reactions including convulsions, hypotension, arrhythmias, and cardiorespiratory depression.', 'Metabolism', 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500142, 'Voxelotor', 'Levacetylmethadol', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and duration of action of levomethadyl acetate, which is a substrate of the isoenzyme. High plasma levels of levomethadyl acetate may increase the risk of QT interval prolongation, which is thought to be associated with serious cardiac events such as ventricular arrhythmias (e.g., torsade de pointes, ventricular tachycardia), cardiac arrest, and sudden death. Cases of QT prolongation and torsade de pointes have been reported with levomethadyl acetate alone during postmarketing use.', 'DDInter', 1767317477, 'Concomitant use of levomethadyl acetate with CYP450 3A4 inhibitors is considered contraindicated.', 'Metabolism', 'Concomitant use of levomethadyl acetate with CYP450 3A4 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500143, 'Voxelotor', 'Lomitapide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lomitapide, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors such as alprazolam, amiodarone, amlodipine, atorvastatin, bicalutamide, cilostazol, cimetidine, cyclosporine, dasatinib, fluoxetine, fluvoxamine, ginkgo, goldenseal, isoniazid, ivacaftor, lapatinib, nilotinib, pazopanib, ranitidine, ranolazine, ticagrelor, tipranavir/ritonavir, and zileuton.', 'Metabolism', 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors such as alprazolam, amiodarone, amlodipine, atorvastatin, bicalutamide, cilostazol, cimetidine, cyclosporine, dasatinib, fluoxetine, fluvoxamine, ginkgo, goldenseal, isoniazid, ivacaftor, lapatinib, nilotinib, pazopanib, ranitidine, ranolazine, ticagrelor, tipranavir/ritonavir, and zileuton.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500144, 'Voxelotor', 'Lonafarnib', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lonafarnib.', 'DDInter', 1767317477, 'Concomitant use of lonafarnib with potent or moderate CYP450 3A4 inhibitors is considered contraindicated.', 'Metabolism', 'Concomitant use of lonafarnib with potent or moderate CYP450 3A4 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500145, 'Voxelotor', 'Methysergide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. The interaction has occurred in patients receiving ergotamine or dihydroergotamine with potent CYP450 3A4 inhibitors such as macrolide antibiotics and protease inhibitors.', 'DDInter', 1767317477, 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors. Patients should be monitored for potential ergot toxicity including peripheral vasospasm, ischemia, thrombosis, tachycardia, and hypertension.', 'Metabolism', 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500146, 'Voxelotor', 'Nafcillin', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations and pharmacologic effects of voxelotor. The proposed mechanism is accelerated clearance of voxelotor due to induction of the CYP450 3A4 isoenzyme, which is the primary route of elimination of voxelotor.', 'DDInter', 1767317477, 'The use of voxelotor with moderate to potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible. If coadministration is required, the manufacturer recommends increasing the voxelotor dose to 2500 mg once daily. Pharmacologic response to voxelotor should also be monitored more closely.', 'Metabolism', 'The use of voxelotor with moderate to potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500147, 'Voxelotor', 'Paclitaxel (protein-bound)', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 3A4 may increase the plasma concentrations of paclitaxel, which has been found in vitro to be a substrate of these isoenzymes.', 'DDInter', 1767317477, 'Clinicians should consider the potential for interaction with drugs that inhibit CYP450 2C8 and/or 3A4 and monitor for evidence of dose-related toxicities of paclitaxel during coadministration, including diarrhea, mucositis, myelosuppression, and peripheral neuropathy.', 'Metabolism', 'Clinicians should consider the potential for interaction with drugs that inhibit CYP450 2C8 and/or 3A4 and monitor for evidence of dose-related toxicities of paclitaxel during coadministration, including diarrhea, mucositis, myelosuppression, and peripheral neuropathy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500148, 'Voxelotor', 'Polatuzumab vedotin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase exposure to unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of polatuzumab vedotin. MMAE is a substrate for CYP450 3A4.', 'DDInter', 1767317477, 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors. Patients should be monitored for development or exacerbation of toxicities such as peripheral neuropathy, myelosuppression, opportunistic infections, tumor lysis syndrome and hepatotoxicity, and the dosing of polatuzumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500149, 'Voxelotor', 'Prednisolone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of corticosteroids, which are primarily metabolized by the isoenzyme. The interaction has been reported with potent inhibitors such as clarithromycin, erythromycin, itraconazole, nefazodone, cobicistat, and ritonavir during concomitant use of various corticosteroids, including inhaled, nasal, and ophthalmic formulations. Systemic corticosteroid adverse effects may occur following intensive or long-term continuous ophthalmic corticosteroid therapy. Cushing''s syndrome and adrenal insufficiency have been attributed to the interaction.', 'DDInter', 1767317477, 'The possibility of increased corticosteroid effects should be considered during coadministration with potent and moderate CYP450 3A4 inhibitors. When indicated for intranasal or inhalational use, alternative corticosteroids such as beclomethasone, which is less dependent on CYP450 3A4 metabolism, should be considered, particularly if long term treatment is required. Patients should be monitored for signs and symptoms of hypercorticism such as acne, striae, thinning of the skin, easy bruising, moon facies, dorsocervical "buffalo" hump, truncal obesity, increased appetite, acute weight gain, edema, hypertension, hirsutism, hyperhidrosis, proximal muscle wasting and weakness, glucose intolerance, exacerbation of preexisting diabetes, depression, and menstrual disorders. Other systemic glucocorticoid effects may include adrenal suppression, immunosuppression, posterior subcapsular cataracts, glaucoma, bone loss, and growth retardation in children and adolescents. Following extensive use with a potent CYP450 3A4 inhibitor, a progressive dosage reduction may be required over a longer period if the corticosteroid is to be withdrawn from therapy, as there may be a significant risk of adrenal suppression.', 'Metabolism', 'The possibility of increased corticosteroid effects should be considered during coadministration with potent and moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500150, 'Voxelotor', 'Quinidine', 'Moderate', 'Coadministration with voxelotor may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 3A4 by voxelotor. The interaction may be significant for sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index.', 'DDInter', 1767317477, 'Concomitant use of voxelotor with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., cisapride, ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, pimozide, triazolam, vinca alkaloids) is not recommended.', 'Metabolism', 'Concomitant use of voxelotor with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
