INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500278, 'Zafirlukast', 'Sibutramine', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sibutramine and its active metabolites, which are metabolized by the isoenzyme. Inhibitors such as ketoconazole, cimetidine, and erythromycin have caused moderate increases in the peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of the active metabolites.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500279, 'Zafirlukast', 'Valdecoxib', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 and/or 3A4 may increase the plasma concentrations of valdecoxib, which is metabolized by these isoenzymes.', 'DDInter', 1767317477, 'The possibility of prolonged and/or increased pharmacologic effects of valdecoxib or parecoxib, including serious adverse effects such as gastrointestinal ulceration and bleeding, should be considered during concomitant therapy with CYP450 2C9 or 3A4 inhibitors, particularly combination (2C9/3A4) inhibitors such as fluconazole, fluvoxamine, imatinib, and zafirlukast. Dose reductions of the COX-2 inhibitor may be required.', 'Metabolism', 'The possibility of prolonged and/or increased pharmacologic effects of valdecoxib or parecoxib, including serious adverse effects such as gastrointestinal ulceration and bleeding, should be considered during concomitant therapy with CYP450 2C9 or 3A4 inhibitors, particularly combination (2C9/3A4) inhibitors such as fluconazole, fluvoxamine, imatinib, and zafirlukast.', NULL, NULL, 'Moderate', 'pharmacodynamic');
