INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500424, 'Fedratinib', 'Zanubrutinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of zanubrutinib, which is primarily metabolized by the isoenzyme. Increased zanubrutinib exposure may potentiate the risk of toxicities such as hemorrhage, infection, cytopenias, malignancies, and atrial fibrillation or flutter.', 'DDInter', 1767317477, 'The manufacturer recommends reducing the dosage of zanubrutinib to 80 mg twice daily when coadministered with moderate CYP450 3A4 inhibitors. Patients should be closely monitored for development of zanubrutinib-related toxicities, and further dosage adjustments made or treatment withheld as needed in accordance with the product labeling. Following discontinuation of the CYP450 3A4 inhibitor, the previous dosage of zanubrutinib should be resumed.', 'Metabolism', 'The manufacturer recommends reducing the dosage of zanubrutinib to 80 mg twice daily when coadministered with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
