INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500571, 'Aminophylline', 'Zileuton', 'Moderate', 'Coadministration with zileuton may significantly increase the plasma concentrations of theophylline. The proposed mechanism is zileuton inhibition of theophylline metabolism via CYP450 1A2.', 'DDInter', 1767317477, 'Caution is advised if theophylline is prescribed in combination with zileuton, particularly in the elderly and patients with chronic obstructive pulmonary disease, congestive heart failure or cirrhosis, as these patients generally have lower theophylline clearance rates and may be particularly vulnerable to theophylline toxicity. When zileuton is initiated in patients receiving theophylline, the theophylline dosage should be reduced by approximately one-half and theophylline levels closely monitored. Similarly, when initiating therapy with theophylline in patients receiving zileuton, the maintenance dosage and/or dosing interval of theophylline should be adjusted accordingly and guided by serum theophylline levels. Patients should be advised to notify their physician if they experience signs and symptoms suggestive of theophylline toxicity such as nausea, vomiting, diarrhea, headache, tremor, irritability, insomnia, seizures, palpitation, or arrhythmia.', 'Metabolism', 'Caution is advised if theophylline is prescribed in combination with zileuton, particularly in the elderly and patients with chronic obstructive pulmonary disease, congestive heart failure or cirrhosis, as these patients generally have lower theophylline clearance rates and may be particularly vulnerable to theophylline toxicity.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500572, 'Zileuton', 'Astemizole', 'Moderate', 'Zileuton is believed to be a weak inhibitor of cytochrome P450 3A4 enzymes, of which astemizole is a known substrate. Increased levels of astemizole may increase the potential for cardiotoxic effects, including life-threatening torsade de pointes.', 'DDInter', 1767317477, 'The coadministration of zileuton with astemizole should be avoided. Nonsedating antihistamines such as fexofenadine, loratadine, and cetirizine may be safer alternatives due to a lack of cardiotoxicity.', 'Metabolism', 'The coadministration of zileuton with astemizole should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500573, 'Zileuton', 'Bedaquiline', 'Moderate', 'Coadministration of bedaquiline with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', 'DDInter', 1767317477, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; other antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; ketolide and macrolide antibiotics; interferons; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), especially in patients with diminished hepatic reserve. Patients treated with bedaquiline should have serum ALT, AST, alkaline phosphatase, and bilirubin monitored at baseline and monthly during treatment, or as often as needed. An increase of serum aminotransferases to greater than 3 times ULN should be followed by repeat testing within 48 hours. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Discontinue bedaquiline if aminotransferase elevations are accompanied by total bilirubin elevation greater than 2 times ULN, aminotransferase elevations are greater than 8 times ULN, or aminotransferase elevations persist beyond 2 weeks.', 'Synergism', 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500574, 'Zileuton', 'Bepridil', 'Minor', 'Zileuton is believed to be a weak inhibitor of cytochrome P450 3A4 enzymes, of which calcium channel blockers are a known substrate. Although no studies have been conducted to verify or quantify the interaction, caution is recommended when using this combination until further data is available.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500575, 'Zileuton', 'Berotralstat', 'Moderate', 'Coadministration with berotralstat may increase the plasma concentrations and effects of drugs that are substrates of CYP450 3A4 and/or 2D6. The mechanism is decreased clearance due to inhibition of CYP450 3A4 and 2D6 activity by berotralstat.', 'DDInter', 1767317477, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', 'Metabolism', 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', NULL, NULL, 'Moderate', 'pharmacodynamic');
