INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1500602, 'Amphotericin B (cholesteryl sulfate)', 'Ziprasidone', 'Major', 'Ziprasidone can cause dose-related prolongation of the QT interval. While clinical data are lacking, the coadministration of ziprasidone and agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential.', 'DDInter', 1767317477, 'Caution is advised when ziprasidone must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with ziprasidone. Patients should be advised to notify their physician if they experience possible signs of an electrolyte imbalance, such as weakness, lethargy, drowsiness, confusion, muscle pains or cramps, dizziness, nausea, vomiting, tachycardia, or an irregular heartbeat.', 'Synergism', 'Caution is advised when ziprasidone must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, NULL, 'Major', 'pharmacodynamic');
