INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501431, 'Etonogestrel', 'Entrectinib', 'Moderate', 'RECOMMENDED: Coadministration with entrectinib may render systemic hormonal contraceptives ineffective. The mechanism for the potential interaction has not been established and there are no clinical data regarding the use of entrectinib with hormonal contraceptives. Physiologically based pharmacokinetic simulation does not predict a significant effect of entrectinib on the pharmacokinetics of ethinyl estradiol. Entrectinib can cause fetal harm when administered to a pregnant woman.', 'DDInter', 1767317477, 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 5 weeks after the final dose. In addition, men receiving entrectinib should use highly effective contraceptive methods during treatment and for 3 months after the final dose.', 'Others', 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 5 weeks after the final dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
