INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501464, 'Granisetron', 'Entrectinib', 'Moderate', 'Entrectinib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Coadministration of entrectinib with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Patients should have electrocardiograms (ECGs) performed prior to initiation of entrectinib and periodically during treatment as appropriate based on individual risk factors.', 'Synergism', 'Coadministration of entrectinib with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
