INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501497, 'Entrectinib', 'Norgestrel', 'Moderate', 'RECOMMENDED: Coadministration with entrectinib may render systemic hormonal contraceptives ineffective. The mechanism for the potential interaction has not been established and there are no clinical data regarding the use of entrectinib with hormonal contraceptives. Physiologically based pharmacokinetic simulation does not predict a significant effect of entrectinib on the pharmacokinetics of ethinyl estradiol. Entrectinib can cause fetal harm when administered to a pregnant woman.', 'DDInter', 1767317477, 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 5 weeks after the final dose. In addition, men receiving entrectinib should use highly effective contraceptive methods during treatment and for 3 months after the final dose.', 'Others', 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 5 weeks after the final dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501498, 'Entrectinib', 'Papaverine', 'Major', 'Intracoronary administration of papaverine has been associated with QT interval prolongation and torsade de pointes (TdP) arrhythmia. The risk may theoretically increase in patients receiving concomitant medications that can also prolong the QT interval or cause bradycardia. The precise mechanism of papaverine-induced ventricular tachyarrhythmias has not been delineated, but may involve inhibition of potassium currents and prolongation of the action potential duration.', 'DDInter', 1767317477, 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', 'Synergism', 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', NULL, NULL, 'Major', 'pharmacodynamic');
