INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501551, 'Enzalutamide', 'Dicoumarol', 'Moderate', 'Apalutamide and Enzalutamide may reduce the effects of oral anticoagulants. The proposed mechanism is induction of the enzyme CYP450 2C9.', 'DDInter', 1767317477, 'Concomitant use of apalutamide or enzalutamide with warfarin and coumarin-like anticoagulants should be avoided. If concomitant therapy is required, additional INR monitoring should be conducted and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation, or change of dosage of enzalutamide in patients who are stabilized on their anticoagulant regimen. Patients should be monitored for altered anticoagulation and advised to promptly report any signs of blood clots (e.g., chest pain, shortness of breath, and sudden loss of vision, or pain, redness or swelling in an extremity) to their physician.', 'Metabolism', 'Concomitant use of apalutamide or enzalutamide with warfarin and coumarin-like anticoagulants should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501552, 'Enzalutamide', 'Doxorubicin (liposomal)', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of doxorubicin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of doxorubicin with CYP450 3A4 inducers should generally be avoided, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. Close monitoring for potentially reduced efficacy of doxorubicin is recommended if coadministration is required.', 'Metabolism', 'Concomitant use of doxorubicin with CYP450 3A4 inducers should generally be avoided, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501553, 'Enzalutamide', 'Echinacea', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', 'DDInter', 1767317477, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
