INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501557, 'Erdafitinib', 'Enzalutamide', 'Major', 'Coadministration with potent inducers of CYP450 2C9 or 3A4 may decrease the plasma concentrations and therapeutic effects of erdafitinib. The mechanism is induction of CYP450 2C9- or 3A4-mediated metabolism of erdafitinib, which has been shown to be metabolized by these isoenzymes.', 'DDInter', 1767317477, 'Concomitant use of erdafitinib with potent inducers of CYP450 2C9 or 3A4 should be avoided.', 'Metabolism', 'Concomitant use of erdafitinib with potent inducers of CYP450 2C9 or 3A4 should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
