INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501643, 'Enzalutamide', 'Nafcillin', 'Moderate', 'Coadministration with inducers of CYP450 2C8 and/or 3A4 may decrease the plasma concentrations of enzalutamide, which is primarily metabolized by CYP450 2C8 to its pharmacologically active metabolite, N-desmethyl enzalutamide, and to a lesser extent by CYP450 3A4.', 'DDInter', 1767317477, 'Concomitant use of enzalutamide with potent CYP450 2C8 and/or 3A4 inducers such as carbamazepine, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided. Moderate inducers such as bosentan, efavirenz, etravirine, modafinil, nafcillin, and nevirapine should also be avoided if possible. The extent to which other, less potent CYP450 3A4 inducers may interact with enzalutamide is unknown. Caution is advised if they are used with enzalutamide.', 'Metabolism', 'Concomitant use of enzalutamide with potent CYP450 2C8 and/or 3A4 inducers such as carbamazepine, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501644, 'Enzalutamide', 'Norgestrel', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 1767317477, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501645, 'Enzalutamide', 'Paclitaxel (protein-bound)', 'Moderate', 'Theoretically, coadministration with drugs that are inducers of CYP450 2C8 and/or 3A4 may decrease the plasma concentrations of paclitaxel, which is metabolized by these isoenzymes.', 'DDInter', 1767317477, 'Clinicians should recognize the potential for interaction with drugs that induce CYP450 2C8 and/or 3A4 and monitor for evidence of reduced therapeutic response to paclitaxel during coadministration.', 'Metabolism', 'Clinicians should recognize the potential for interaction with drugs that induce CYP450 2C8 and/or 3A4 and monitor for evidence of reduced therapeutic response to paclitaxel during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501646, 'Enzalutamide', 'Papaverine', 'Major', 'Intracoronary administration of papaverine has been associated with QT interval prolongation and torsade de pointes (TdP) arrhythmia. The risk may theoretically increase in patients receiving concomitant medications that can also prolong the QT interval or cause bradycardia. The precise mechanism of papaverine-induced ventricular tachyarrhythmias has not been delineated, but may involve inhibition of potassium currents and prolongation of the action potential duration.', 'DDInter', 1767317477, 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', 'Synergism', 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501647, 'Enzalutamide', 'Perphenazine', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 1767317477, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501648, 'Enzalutamide', 'Phenobarbital', 'Moderate', 'Coadministration with inducers of CYP450 2C8 and/or 3A4 may decrease the plasma concentrations of enzalutamide, which is primarily metabolized by CYP450 2C8 to its pharmacologically active metabolite, N-desmethyl enzalutamide, and to a lesser extent by CYP450 3A4.', 'DDInter', 1767317477, 'Concomitant use of enzalutamide with potent CYP450 2C8 and/or 3A4 inducers such as carbamazepine, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided. Moderate inducers such as bosentan, efavirenz, etravirine, modafinil, nafcillin, and nevirapine should also be avoided if possible. The extent to which other, less potent CYP450 3A4 inducers may interact with enzalutamide is unknown. Caution is advised if they are used with enzalutamide.', 'Metabolism', 'Concomitant use of enzalutamide with potent CYP450 2C8 and/or 3A4 inducers such as carbamazepine, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1501649, 'Enzalutamide', 'Phenolphthalein', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 1767317477, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
