INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1502068, 'Epoprostenol', 'Methyldopa', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', 'DDInter', 1767317477, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1502069, 'Epoprostenol', 'Minoxidil (topical)', 'Moderate', 'Theoretically, topical administration of minoxidil may potentiate the hypotensive effects of systemic minoxidil and other vasodilators. Increased systemic absorption may occur when topical minoxidil is used in excess of normally recommended dosages or applied to compromised skin.', 'DDInter', 1767317477, 'Patients prescribed topical minoxidil in combination with systemic vasodilators or peripherally-acting antiadrenergic agents should be monitored for orthostatic signs and symptoms, including increase in heart rate, decrease in standing blood pressure, dizziness, and headache.', 'Synergism', 'Patients prescribed topical minoxidil in combination with systemic vasodilators or peripherally-acting antiadrenergic agents should be monitored for orthostatic signs and symptoms, including increase in heart rate, decrease in standing blood pressure, dizziness, and headache.', NULL, NULL, 'Moderate', 'pharmacodynamic');
