INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1502104, 'Everolimus', 'Eprosartan', 'Moderate', 'The use of mTOR (mammalian target of rapamycin) inhibitors has been associated with the development of angioedema, and coadministration with other drugs that are also known to cause angioedema such as ACE inhibitors, angiotensin II receptor antagonists, and renin inhibitors may increase the risk.', 'DDInter', 1767317477, 'Clinicians and patients should be aware of the potential for increased risk of angioedema when mTOR inhibitors are prescribed with other drugs that are also known to cause angioedema. Patients should be advised to promptly discontinue these medications and seek medical attention if they develop signs or symptoms suggestive of angioedema such as swelling of the face, extremities, eyes, lips, or tongue, and difficulty swallowing or breathing.', 'Synergism', 'Clinicians and patients should be aware of the potential for increased risk of angioedema when mTOR inhibitors are prescribed with other drugs that are also known to cause angioedema.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1502105, 'Exenatide', 'Eprosartan', 'Moderate', 'Drugs that affect renal function and/or hydration status may potentiate the adverse renal effects of exenatide. The mechanism of interaction is unknown. Exenatide has not been found to be directly nephrotoxic in preclinical or clinical studies.', 'DDInter', 1767317477, 'Clinicians should be aware of the potential for increased adverse renal effects of exenatide during coadministration with drugs that are known to affect renal function or hydration status.', 'Others', 'Clinicians should be aware of the potential for increased adverse renal effects of exenatide during coadministration with drugs that are known to affect renal function or hydration status.', NULL, NULL, 'Moderate', 'pharmacodynamic');
