INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1502519, 'Erlotinib', 'Ranitidine (bismuth citrate)', 'Moderate', 'INTERVAL: Concurrent administration of agents that increase gastric pH such as H2-receptor antagonists may decrease the oral bioavailability of erlotinib and reduce its concentrations in plasma. The solubility of erlotinib decreases when the pH is over 5, resulting in reduced absorption.', 'DDInter', 1767317477, 'In general, the concomitant use of erlotinib with agents that increase gastric pH should be avoided whenever possible. If acid-suppression therapy is required during erlotinib treatment, an H2-receptor antagonist such as ranitidine may be considered. Available pharmacokinetic data suggest that erlotinib should be administered once a day, at least 2 hours before or 10 hours after dosing of the H2-receptor antagonist. Additionally, the lowest effective dosage of the H2-receptor antagonist should be prescribed. It may be appropriate to avoid using cimetidine for long-term acid suppression, as it may inhibit the CYP450 3A4-mediated metabolism of erlotinib and increase the risk of toxicity.', 'Metabolism, Absorption', 'In general, the concomitant use of erlotinib with agents that increase gastric pH should be avoided whenever possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1502520, 'Erlotinib', 'Riociguat', 'Moderate', 'Coadministration with inhibitors of CYP450 1A1 may theoretically increase the plasma concentrations of riociguat, which is partially metabolized by the isoenzyme to its main active metabolite (1/10th to 1/3rd the pharmacologic activity of riociguat). Increased levels of riociguat may increase the risk for hypotension.', 'DDInter', 1767317477, 'Caution is advised if riociguat is prescribed in combination with an inhibitor of CYP450 1A1. Blood pressure should be monitored, and a dose reduction of riociguat should be considered if necessary.', 'Metabolism', 'Caution is advised if riociguat is prescribed in combination with an inhibitor of CYP450 1A1.', NULL, NULL, 'Moderate', 'pharmacodynamic');
