        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1503141, 'Esmolol', 'Ritodrine', 'Major', 'The use of a nonselective beta blocker with ritodrine (a beta-2 agonist) may result in mutually antagonistic effects, negating the effect of one or both drugs.', NULL, 'In general, the use of beta blockers with ritodrine is discouraged.', NULL, 'In general, the use of beta blockers with ritodrine is discouraged. Selective beta blockers (e.g. acebutolol, atenolol, betaxolol, bisoprolol, metoprolol) may be preferable if the use of a beta blocker is unavoidable.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
