INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1503342, 'Dienogest', 'Armodafinil', 'Moderate', 'RECOMMENDED: Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of ethinyl estradiol and other contraceptive hormones. The mechanism involves induction of CYP450 3A4-mediated metabolism by modafinil and armodafinil.', NULL, 'Women using hormonal contraceptives (including depot and implantable contraceptives) should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with modafinil or armodafinil.', NULL, 'Women using hormonal contraceptives (including depot and implantable contraceptives) should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with modafinil or armodafinil. Alternative or additional methods of birth control are recommended during and for one month after treatment with modafinil or armodafinil. Intrauterine systems are unlikely to be significantly affected because of their local action. Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
